import React from "react";
import "./Home.css";
import User from "../User/User";
import sampleAvatar from "../../assets/PIC.jpg";
import Post from "../Post/Post";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getFollowingPosts } from "../../Actions/User";
import Loader from "../Loader/Loader";
import { Typography } from "@mui/material";

const Home = () => {

    const dispatch = useDispatch();

    const {loading, posts, error} = useSelector(state=>state.postOfFollowing);

    useEffect (() => {
        dispatch(getFollowingPosts());
    }, []);

  return (
   loading ? <Loader/> : (
    <div className="home">
    <div className="homeleft">
       {
        posts && posts.length > 0 ? posts.map(() => {
            <Post postImage="https://media.istockphoto.com/id/1257951336/photo/transparent-umbrella-under-rain-against-water-drops-splash-background-rainy-weather-concept.jpg?s=612x612&w=0&k=20&c=lNvbIw1wReb-owe7_rMgW8lZz1zElqs5BOY1AZhyRXs=" 
            ownerName={"Chaieeetanya"}
            caption={"this is the sample post"}
            />
        }) : <Typography variant="h6">No posts yet</Typography>
       }
    </div>
    <div className="homeright">
        <User 
            userId={"user._id"}
            name={"Chaitanya"}
            avatar={sampleAvatar}
        />
    </div>
</div>
   )
  )
}
export default Home;
