import React, {useState, useEffect} from "react";
import "./Post.css";
import { MoreVert, Favorite, FavoriteBorder, ChatBubbleOutline, DeleteOutline} from "@mui/icons-material"
import { Link } from "react-router-dom";
import { Avatar, Button, Typography } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import { likePost } from "../../Actions/Post";
import { useAlert } from "react-alert";

const Post = ({
    postId, 
    caption, 
    postImage, 
    likes=[], 
    comments=[], 
    ownerImage, 
    ownerName, 
    ownerId, 
    isDelete = false, 
    isAccount = false}) => {

        const [liked, setLiked] = useState(false);

        const {error, message} = useSelector((state) => state.like);

        const dispatch = useDispatch();
        const alert = useAlert();

        const handleLike = () => {
            setLiked(!liked);
            dispatch(likePost(postId));
        };

        useEffect(() => {
            if(error) {
                alert.error(error);
            } 
            if(message) {
                alert.error(message);
            } 
        }, [alert, error, message]);

  return (
    <div className="post">

        <div className="postHeader">
            {isAccount ? <Button>
                <MoreVert/>
            </Button> : null}

        </div>
        <img src={postImage} alt="Post" />

        <div className="postDetails">
            <Avatar src={ownerImage} alt="User" sx={{
                height: "3vmax",
                width: "3vmax",
            }}/>

            <Link to={`user/${ownerId}`}>
                <Typography fontWeight={700}>{ownerName}</Typography>
            </Link>

            <Typography fontWeight={100} color="rgba(0, 0, 0, 0.582)" style={{alignSelf: "center"}}>
                {caption}
            </Typography>
        </div>
        <button style={{
            border:"none",
            backgroundColor: "white",
            cursor: "pointer",
            margin: "1vmax 2vmax"
        }}>
            <Typography>5 likes</Typography>
        </button>

        <div className="postFooter">
            <Button onClick={handleLike}>
                {
                    liked ? <Favorite style={{color:"red"}}/> : <FavoriteBorder/>
                }
            </Button>
            <Button>
                <ChatBubbleOutline/>
            </Button>
            
            {isDelete ? (
                    <Button>
                        <DeleteOutline/>
                    </Button>
                ) : null}
        </div>

    </div>
  )
}
export default Post;
