import React, { useEffect, useState} from "react";
import "./Account.css";
import { useDispatch, useSelector } from "react-redux";
import { getMyPosts } from "../../Actions/User";
import { useAlert } from "react-alert";
import Post from "../Post/Post";
import { Avatar, Button, Typography, Dialog } from "@mui/material";
import Loader from "../Loader/Loader";
import { Link } from "react-router-dom";
import User from "../User/User";


const Account = () => {
    const dispatch = useDispatch();
    const alert = useAlert();

    const {user, loading:userLoading} = useSelector((state) => state.user);
    const { loading, error, posts} = useSelector((state) => state.myPosts);
    const { error:likeError, message , loading:deleteLoading} = useSelector((state) => state.like);

    const [followersToggle, setFollowersToggle] = useState(false);
    const [followingToggle, setFollowingToggle] = useState(false);

    useEffect(() => {
        dispatch(getMyPosts());
    }, [dispatch]);

    useEffect(() => {
        if (error) {
          alert.error(error);
          dispatch({ type: "clearErrors" });
        }
        if (likeError) {
          alert.error(likeError);
          dispatch({ type: "clearErrors" });
        }
        if (message) {
          alert.success(message);
          dispatch({ type: "clearMessage" });
        }
      }, [alert, error, message, likeError, dispatch]);

  return (
    loading===true || userLoading===true ? (
    <Loader/> 
    ) : (
    <div className="account">
        <div className="accountleft">
        {posts && posts.length > 0 ? (
          posts.map((post) => (
            <Post
                key={post._id}
                postId={post._id}
                caption={post.caption}
                postImage={post.image.url}
                likes={post.likes}
                comments={post.comments}
                ownerImage={post.owner.avatar.url}
                ownerName={post.owner.name}
                ownerId={post.owner._id}
                isAccount={true}
                isDelete={true}
            />
          ))
        ) : (
          <Typography variant="h6">You have not made any post</Typography>
        )}
        </div>
        <div className="accountright">
            <Avatar 
                src={user.avatar.url}
                sx={{ height: "8vmax", width: "8vmax"}}
            />

            <Typography variant="h5">{user.name}</Typography>

            <div>
                <button onClick={()=>setFollowersToggle(!followersToggle)}>
                    <Typography>Followers</Typography>
                </button>
                <Typography>{user.followers.length}</Typography>
            </div>

            <div>
                <button>
                    <Typography>Following</Typography>
                </button>
                <Typography>{user.following.length}</Typography>
            </div>

            <div>
                <Typography>Post</Typography>
                <Typography>{user.posts.length}</Typography>
            </div>

            <Button variant="contained">Logout</Button>
        
            <Link to="/update/profile"> Edit Profile</Link>
            <Link to="/update/password"> Change Password</Link>
       
            <Button
            variant="text" style={{ color: "red", margin: "2vmax"}}>
                Delete My Profile
            </Button>

            <Dialog open={followersToggle} onClose={()=>setFollowersToggle(!followersToggle)}>
            <div className="Dialogbox">
                <Typography variant="h4">Followers</Typography>
                {user && user.followers.length > 0 ? ( user.followers.map((follower) => (
                    <User
                        key={follower._id}
                        userId={follower._id}
                        name={follower.name}
                        avatar={follower.avatar.url}
                      />
                    ))
                ):(
                    <Typography>You have no followers</Typography>
                )}
            </div>
        </Dialog>
            <Dialog open={followingToggle} onClose={()=>setFollowingToggle(!followersToggle)}>
            <div className="Dialogbox">
                <Typography variant="h4">Following</Typography>
                {user && user.following.length > 0 ? ( user.following.map((follow) => (
                    <User
                        key={follow._id}
                        userId={follow._id}
                        name={follow.name}
                        avatar={follow.avatar.url}
                      />
                    ))
                ):(
                    <Typography>You're not following anyone</Typography>
                )}
            </div>
        </Dialog>
        </div>
    </div>
    )
  )
};

export default Account
