import axios from "axios";


export const likePost = async (id) => {
    try {
        
        dispatch({
            type: "likeRequest"
        });

        const {data} = await axios.get(`/api/v1/post/${id}`);

        dispatch({
            type: "likeSuccess",
            payload: data.message
        });
    } catch (error) {
        dispatch({
            type: "likeFailure",
            payload: error.response.data.message
        });
    }
}
